package week1day1;

public class LearnOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

int x=25;
int y=40;

System.out.println(x+y);
System.out.println(y-x);
System.out.println(y*x);
System.out.println(y/x);

System.out.println(x>y);
System.out.println(++x);

System.out.println(x++);
System.out.println(x);


	
	
	}

}
